      )))
    } else {
      as.character(html(glue(
        "{origin_airports_iata} - {origin_airports_name} {get_flag(origin_airports_countryiso2)}"
      )))
    }
  ) |>
  mutate(
    destination_info = if (
      destination_plausible == 0 & is.na(destination_airports_name)
    ) {
      as.character(html(glue(
